import React, { useEffect, useState } from 'react';
import { Collapse } from 'antd';
import { DeleteOutlined, EditOutlined } from '@ant-design/icons';
import AdminLayout from '../../../components/admin/AdminLayout';
import '../../../components/styles/app.scss';
import { getAllQuery } from '../../../services/apis/contact';

const { Panel } = Collapse;

export default function CheckQuery() {
  const [queries, setQueries] = useState({});

  useEffect(() => {
    getAllQuery().then((res) => res && res.data && setQueries(res.data));
  }, []);
  const handleDeleteCategory = (id) => {
    // deleteCategory(id).then();
  };

  return (
    <AdminLayout>
      <h3>Check Queries ({queries.count})</h3>
      <Collapse expandIconPosition="right">
        {queries &&
          queries.data &&
          queries.data.map((c, i) => {
            let date = new Date(c.createdAt);
            let istDate = date.toUTCString();
            return (
              <Panel
                showArrow={false}
                header={`  ${c.subject}  ${istDate}`}
                key={i}
                extra={
                  <>
                    <EditOutlined />
                    <DeleteOutlined
                      onClick={() => handleDeleteCategory(c._id)}
                    />
                  </>
                }
              >
                <div>#{c._id}</div>
                <div>
                  {/* {c.subcategories.map((s, si) => (
                <span>{s.name}</span>
              ))} */}
                </div>
              </Panel>
            );
          })}
      </Collapse>
    </AdminLayout>
  );
}
