import dynamic from 'next/dynamic';
import React, { useEffect, useState } from 'react';
import ImgsViewer from 'react-images-viewer';
import { Spin } from 'antd';
import { useParams } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';

export default function ProductDetail(props) {
  const { data } = props;
  console.log('data', data);

  const [openViewer, setOpenViewer] = useState(false);
  const [currImg, setCurrImg] = useState(0);
  const [activeImg, setActiveImg] = useState(0);

  const imgSrc =
    data &&
    data.images.map((i) => {
      return { src: `data:image/png;base64,${i.data}` };
    });
  const gotoPrevImg = () => setCurrImg((s) => s - 1);
  const gotoNextImg = () => setCurrImg((s) => s + 1);

  const closeImg = () => {
    setOpenViewer(false);
    setCurrImg(0);
  };

  if (data)
    return (
      <Spin spinning={!data}>
        {data && (
          <div className="c-product-detail row">
            <div className="c-product-detail__left col-6">
              <ImgsViewer
                currImg={currImg}
                imgs={imgSrc}
                isOpen={openViewer}
                onClickPrev={gotoPrevImg}
                onClickNext={gotoNextImg}
                onClose={closeImg}
              />
              <img
                src={imgSrc[activeImg].src}
                alt="main_image"
                onClick={() => setOpenViewer(true)}
                className="c-product-detail__image"
              />
              <div>
                {imgSrc.map((im, index) => (
                  <img
                    key={index}
                    src={im.src}
                    alt="mini"
                    className="c-product-detail__mini-image"
                    onClick={() => setActiveImg(index)}
                  />
                ))}
              </div>
            </div>
            <div className="c-product-detail__right col-6">name</div>
          </div>
        )}
      </Spin>
    );
  return <div></div>;
}
