import React, { useState, useEffect } from 'react';
import '../../../components/styles/app.scss';
import AdminLayout from '../../../components/admin/AdminLayout';

import { Input, Button, Select } from 'antd';

import {
  createProduct,
  getAllCategories,
} from '../../../services/apis/admin.js';

const { Option } = Select;

//constants
const allSizes = ['32', '34', '36'];
const allColors = ['red', 'green', 'blue'];

export default function create(props) {
  const { editData } = props;

  const [inputData, setInputData] = useState({
    name: '',
    shortDesc: '',
    description: '',
    category: '',
    price: '',
    sizes: [],
    colors: [],
    hidden: false,
    images: [],
  });
  const [imagesURL, setImagesURL] = useState([]);
  const [categories, setCategories] = useState([]);
  const [subCategories, setSubCategories] = useState([]);
  const [selectedSubCategories, setSelectedSubCategories] = useState([]);

  useEffect(() => {
    if (editData) {
      let newInputData = { ...inputData };
      newInputData.sizes = editData.sizes;
      setInputData(newInputData);
    }
  }, []);

  useEffect(() => {
    getAllCategories().then(
      (res) => res && res.data && setCategories(res.data.data)
    );
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log(inputData);
    let formData = new FormData();
    // Object.keys(inputData).forEach((key) => {
    // });

    let tempSubCategories = subCategories.map((c) => c._id);

    formData.set('name', inputData.name);
    formData.set('shortDesc', inputData.shortDesc);
    formData.set('description', inputData.description);
    formData.set('category', inputData.category);
    formData.set('price', inputData.price);
    formData.set('hidden', inputData.hidden);
    formData.set('sizes', JSON.stringify(inputData.sizes));
    formData.set('colors', JSON.stringify(inputData.colors));
    formData.set('subCategories', JSON.stringify(tempSubCategories));

    //for Images
    const fileListAsArray = Array.from(inputData.images);
    for (var i in fileListAsArray) {
      console.log(fileListAsArray[i]);
      formData.append('images', fileListAsArray[i]);
    }
    createProduct(formData);
  };

  const handleChange = (e) =>
    setInputData({ ...inputData, [e.target.name]: e.target.value, error: '' });

  const handleCheckChange = (e) =>
    setInputData({ ...inputData, hidden: e.target.checked });

  const handleCategoryChange = (cate) => {
    setInputData({ ...inputData, category: cate._id, subCategories: [] });
    setSubCategories(cate.subcategories);
    setSelectedSubCategories([]);
  };
  const handleSizeChange = (e) =>
    setInputData({ ...inputData, sizes: e.target.value });

  const handleColorsChange = (e) =>
    setInputData({ ...inputData, colors: e.target.value });

  const handleSubCategoryChange = (value) => {
    setSelectedSubCategories(value);
  };

  const handleImagesChange = (e) => {
    const fileList = e.target.files;
    const fileListURL = [];

    for (var i = 0; i < fileList.length; i++) {
      const file = fileList[i];
      fileListURL.push(URL.createObjectURL(file));
    }

    setInputData({
      ...inputData,
      images: e.target.files,
    });
    setImagesURL(fileListURL);
  };

  return (
    <AdminLayout>
      {categories && categories.length > 0 ? (
        <div>
          <h3> {editData ? ' Edit ' : 'Enter '} Product Details</h3>
          <div className="c-admin-create-product__item">
            <Select
              // onChange={handleCategoryChange}
              style={{ width: '100%' }}
              placeholder="Please select category"
            >
              {categories.map((cate, index) => (
                <Option
                  key={index}
                  value={cate._id}
                  name={cate.name}
                  onClick={() => handleCategoryChange(cate)}
                >
                  {cate.name}
                </Option>
              ))}
            </Select>
          </div>

          <div className="c-admin-create-product__item">
            <Select
              value={selectedSubCategories || []}
              onChange={handleSubCategoryChange}
              mode="multiple"
              placeholder="Select subcategories"
              style={{ width: '100%' }}
            >
              {subCategories.map((subcategory, index) => (
                <Option key={index} value={subcategory._id}>
                  {subcategory.name}
                </Option>
              ))}
            </Select>
          </div>

          <div className="c-admin-create-product__item">
            <Input
              id="name"
              placeholder="Enter Product Name"
              label="Product Name"
              name="name"
              required
              onChange={handleChange}
            />
          </div>

          <div className="c-admin-create-product__item">
            <Input.TextArea
              id="short-desc-input"
              label="Short Description"
              name="shortDesc"
              required
              rows={2}
              onChange={handleChange}
              placeholder="write a short description about the product."
            />
          </div>

          <div className="c-admin-create-product__item">
            <Input.TextArea
              label="Description"
              name="description"
              rows={4}
              onChange={handleChange}
              placeholder="write a full description about the product."
            />
          </div>

          <div className="c-admin-create-product__item">
            <Input
              label="Price ( ₹ )"
              name="price"
              fullWidth
              onChange={handleChange}
            />
          </div>

          <div className="c-admin-create-product__item">
            <Select
              style={{ width: '100%' }}
              placeholder="Please select Sizes"
              onChange={handleSizeChange}
            >
              {allSizes.map((size, index) => (
                <Option key={index} value={size}>
                  {size}
                </Option>
              ))}
            </Select>
          </div>
        </div>
      ) : (
        <div>Create a Category first</div>
      )}
    </AdminLayout>
  );
}
