export const setMessage = (payload) => ({
  type: 'SET_MESSAGE',
  payload,
});
export const showMessage = (payload) => ({
  type: 'SHOW_MESSAGE',
  payload,
});
export const closeMessage = (payload) => ({
  type: 'CLOSE_MESSAGE',
  payload,
});
