import { useHttp } from '../../utilis/useHttp';

export const createContactUs = (data) =>
  useHttp({
    method: 'post',
    url: `/contact`,
    data,
  });
export const getAllQuery = (data) =>
  useHttp({
    method: 'post',
    url: `/contact/query`,
    data,
  });
export const getAllEnquiry = (data) =>
  useHttp({
    method: 'post',
    url: `/contact/enquiry`,
    data,
  });
export const getAllPromotions = (data) =>
  useHttp({
    method: 'post',
    url: `/contact/promotions`,
    data,
  });
