import React, { useState, useEffect } from 'react';
import dynamic from 'next/dynamic';
import '../components/styles/pages/_catalogue.scss';
const Layout = dynamic(() => import('../components/Layouts/Layout'), {
  ssr: false,
});
const Heading = dynamic(() => import('../components/Heading'), {
  ssr: false,
});
import { getCategories, getProducts } from '../redux/actions';
import { useSelector, useDispatch } from 'react-redux';

import { Select, Input, Button } from 'antd';

const { Option } = Select;

export default function ECatalogue() {
  const dispatch = useDispatch();
  const [value, setValue] = useState({
    name: '',
    storeName: '',
    phoneNumber: '',
    address: '',
    city: '',
    state: '',
    category: {},
    subCategory: {},
  });
  const [selectedProducts, setSelectedProducts] = useState([]);

  function handleCategoryChange(v) {
    setValue({ ...value, category: v, subCategory: {} });
  }

  function handleSubCategoryChange(v) {
    setValue({ ...value, subCategory: v });
  }

  const products = useSelector((state) => state.products);

  useEffect(() => {
    dispatch(getCategories());
  }, []);

  const handleLoadProducts = () => {
    dispatch(getProducts());
  };

  return (
    <Layout headerVersions={['bg-dark']} headerTheme="black">
      <div className="c-catalogue">
        {console.log(value)}

        <Heading versions={['shop-all']}>{'Enquiry Form'}</Heading>
        <div className="c-catalogue__options">
          <div className=" row">
            <div className="col-4">
              <p>Name</p>
              <Input placeholder="Enter your name" />
            </div>
            <div className="col-4">
              <p>Store Name</p>
              <Input placeholder="Enter your store name" />
            </div>
            <div className="col-4">
              <p>Phone Number</p>

              <Input placeholder="Enter your phone no" />
            </div>
          </div>
          <div className=" row">
            <div className="col-4">
              <p>Address</p>
              <Input placeholder="Enter your address" />
            </div>
            <div className="col-4">
              <p>City</p>
              <Input placeholder="Enter your city" />
            </div>
            <div className="col-4">
              <p>State</p>
              <Input placeholder="Enter your state" />
            </div>
          </div>
          <div className=" row">
            <div className="col-4">
              <p>Category</p>
              <Select
                value={value.category.name}
                onChange={handleCategoryChange}
              >
                {products.categories.map((cate) => (
                  <Option key={cate._id} value={cate}>
                    {cate.name}
                  </Option>
                ))}
              </Select>
            </div>
            {value.category !== {} && value.category.subcategories && (
              <div className="col-4">
                <p>Sub Category</p>
                <Select
                  value={value.subCategory.name}
                  onChange={handleSubCategoryChange}
                >
                  {value.category.subcategories.map((sCate) => (
                    <Option key={sCate._id} value={sCate}>
                      {sCate.name}
                    </Option>
                  ))}
                </Select>
              </div>
            )}
          </div>
          <div className="row load-btn-wrapper">
            <Button className="load-btn" onClick={handleLoadProducts}>
              Load Products
            </Button>
          </div>
        </div>
        <div className="c-catalogue__products">
          {/* {products.products.map((el, i) => (
            <Fade>
              <div
                key={el._id}
                className="col-lg-4 col-md-6"
                style={{ padding: '0 40px' }}
              >
                <Product data={el} />
              </div>
            </Fade>
          ))} */}
        </div>
      </div>
    </Layout>
  );
}

ECatalogue.getInitialProps = async (ctx) => {
  const res = await fetch('https://api.github.com/repos/vercel/next.js');
  const json = await res.json();
  return { stars: json.stargazers_count };
};
