import { SET_PRODUCTS, SET_PRODUCT, CLEAR_PRODUCT } from '../actions/type';

import {
  getVisibleProducts,
  getFeaturedProduct,
  getCategoriesProducts,
} from '../../services/helpers/product';
const initialState = {
  products: [],
  product: null,
  featured: [],
  categories: [],
};

export default (state = initialState, action) => {
  const { payload, type } = action;
  switch (type) {
    case SET_PRODUCTS:
      console.log('no', action.payload);
      return {
        ...state,
        products: action.payload,
      };

    case SET_PRODUCT:
      return {
        ...state,
        product: payload,
      };

    case CLEAR_PRODUCT:
      return {
        ...state,
        product: null,
      };

    default:
      return state;
  }
};
