import dynamic from 'next/dynamic';
import React, { useEffect, useState } from 'react';
import ImgsViewer from 'react-images-viewer';
import { Spin } from 'antd';
import { useParams } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';

export default function ProductDetail(props) {
  const { data } = props;
  console.log('data', data);

  const [inputData, setInputData] = useState(data);
  const [imgSrc, setImgsrc] = useState([]);
  const [openViewer, setOpenViewer] = useState(false);
  const [currImg, setCurrImg] = useState(0);

  const gotoPrevImg = () => setCurrImg((s) => s - 1);
  const gotoNextImg = () => setCurrImg((s) => s + 1);

  const closeImg = () => {
    setOpenViewer(false);
    setCurrImg(0);
  };

  if (data)
    return (
      <Spin spinning={!data}>
        {data && (
          <div className="c-product-deatail">
            <ImgsViewer
              currImg={currImg}
              imgs={imgSrc}
              isOpen={openViewer}
              onClickPrev={gotoPrevImg}
              onClickNext={gotoNextImg}
              onClose={closeImg}
            />
            <img
              src={imgSrc[0].src}
              alt="main_image"
              onClick={() => setOpenViewer(true)}
              className="c-product-deatail__image"
            />
            {imgSrc.map((im, index) => (
              <img
                key={index}
                src={im.src}
                alt="mini"
                className="c-product-deatail__mini-image"
              />
            ))}
          </div>
        )}
      </Spin>
    );
  return <div></div>;
}
