//     return {
//       category: query.cid,
//       productList: visibleProducts.map((el) => addSlugToProduct(el)),
//       combos: visibleCombo.map((el) => addSlugToProduct(el)),
//       shopdata: Shopdata.shop,
//     };
//   };

export default Products;
